#!/usr/bin/env node

var init = require("./../lib/init.js");
var format = require("./../lib/format.js");
var test = require("./../lib/test.js");
var doc = require("./../lib/doc.js");
var badges = require("./../lib/badges.js");

var subparams;
var value;
var found;

for (var index = 0; index < process.argv.length && !found; index++) {

    // Ignore casing 
    value = process.argv[index].toLocaleLowerCase();

    // Get subparams
    if ((index + 1) < process.argv.length) {
        subparams = process.argv.slice(index + 1, process.argv.length);
    } else {
        subparams = null;
    }

    // Check command
    switch (value) {
        case "init":
            found = true;
            init.init();
            break;
        case "format-js":
            found = true;
            format.prettier(null, subparams);
            break;
        case "format-html":
            found = true;
            format.jsBeautifyHTML(process.argv);
            break;
        case "format-css":
            found = true;
            format.jsBeautifyCSS(process.argv);
            break;
        case "test":
            found = true;
            test.intern(null, subparams);
            break;
        case "doc":
            found = true;
            doc.doc(null, subparams);
            break;
        case "badges":
            found = true;
            badges.create(process.argv);
            break;
    }

}



