var path = require("path");
var commons = require("@vimlet/commons");

var cwd = process.cwd();

exports.intern = function(packagePath, params, cb) {
    if(!params) {
        params = ["config=../tests/intern"]
    }
    commons.run.exec(path.join(cwd, "node_modules/.bin/intern-client"), params, packagePath, null, cb);
};

